mapper.update:
update post
set NEXT_POST_ID = #{nextPostId}
where POST_ID = #{postId}

update post
set  NOW_POST_STUS CD = 'ST00450'
where POST_ID = #{postId};

update post
set  BEF_POST_ID = #{befPostId}
where POST_ID = #{postId};

update post
set  NEXT_POST_ID =  #{nextPostId}
where POST_ID = #{postId};

UPDATE post
SET POST_INQR_CNT = POST_INQR_CNT + 1
WHERE POST_ID = #{postId}

mapper.select:
SELECT p.*, s.SER_TTL, cd.CD_NM as nowPostStusCdNm , u.NIC, tb.STYLE_TEXT FROM post AS p INNER JOIN channel AS c ON p.CHNL_ID = c.CHNL_ID INNER JOIN common_code as cd ON p.NOW_POST_STUS_CD = cd.CD LEFT JOIN series as s ON p.SER_ID = s.SER_ID INNER JOIN user as u ON c.CRT_ID = u.USER_ID LEFT JOIN block as b ON b.POST_ID = p.POST_ID LEFT JOIN text_block as tb ON tb.BLK_ID = b.BLK_ID AND b.BLK_KIND_CD = (SELECT CD FROM common_code WHERE CD_NM = '텍스트블록') WHERE s.SER_ID = #{serId}
ORDER BY p.POST_PBLC_DTM DESC
ORDER BY p.POST_LIK_CNT DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT p.*, s.SER_TTL, cd.CD_NM as nowPostStusCdNm , u.NIC as userNic, u.USER_IMG_PATH, tb.STYLE_TEXT, tg.TAG_NM as postType FROM post AS p INNER JOIN channel AS c ON p.CHNL_ID = c.CHNL_ID INNER JOIN common_code as cd ON p.NOW_POST_STUS_CD = cd.CD LEFT JOIN series as s ON p.SER_ID = s.SER_ID INNER JOIN user as u ON c.CRT_ID = u.USER_ID INNER JOIN post_tag as pt on p.POST_ID = pt.POST_ID INNER JOIN tag as tg on pt.TAG_ID = tg.TAG_ID LEFT JOIN block as b ON b.POST_ID = p.POST_ID LEFT JOIN text_block as tb ON tb.BLK_ID = b.BLK_ID AND b.BLK_KIND_CD = (SELECT CD FROM common_code WHERE CD_NM = '텍스트블록') WHERE c.CHNL_URI = #{chnlUri} AND tg.TAG_NM = #{postType}
ORDER BY p.POST_ID DESC -- p.POST_PBLC_DTM DESC
ORDER BY p.POST_LIK_CNT DESC
LIMIT #{pageSize} OFFSET #{offset}

select p.POST_ID, p.BEF_POST_ID, p.NEXT_POST_ID
from post as p
where POST_ID = #{postId}

select p.POST_ID as id
from post as p
where p.SER_ID = #{serId}

select POST_ID
from post
where CHNL_ID = #{chnlId}

select p.*
from post as p
where post_id = #{postId}
